mapper.select:
 select
empid, empname, idcard, sex, email, loc, phone, hiredate, sal, leavedate, e.jobId,
 state, j.jobName
 from emp e left join job j on e.jobid = j.jobId


     empname like '%${empname}%'


     and e.jobId=#{jobid}


     and hiredate>=#{hiredateStart}


     and hiredate <= #{hiredateEnd}
